CREATE VIEW core_event_details_view AS SELECT cta.id as record_id,cu.id user_id,cu.firstname,cu.lastname,cu.username,cu.email,concat(p.first_name,' ',p.last_name) as piname,cta.time_modified as timestamp,cta.start,cta.end,cta.note,cta.state as event_state,cta.service_id,cs.short_name as service_name,cs.state as service_state,cr.name as resource_name FROM core_timed_activity cta, core_users cu,core_services cs,core_resources cr, people p WHERE cu.id = cta.user AND cs.id = cta.service_id AND p.individual_id = cu.pi AND cr.id = cs.resource_id;
